/**
 * StringToAddr
 */
function StringToAddr(str) {
	var addr = new sh.NetAdr();

	if (str.indexOf('localhost') !== -1) {
		addr.type = sh.NetAdrType.LOOPBACK;
	} else {
		addr.type = sh.NetAdrType.IP;
	}

	var split = str.split(':');

	if (!split.length) {
		addr.ip = split[0];
		addr.port = 9000;
	} else {
		addr.ip = split[0];
		addr.port = split[1];
	}

	return addr;
}

