	GroundTrace();
	SetWaterLevel();

	// Weapons.
	UpdateWeapon();

	// Torso animations.
	TorsoAnimation();

	// Footstep events / legs animations.
	FootstepEvents();

	// Entering / leaving water splashes.
	WaterEvents();

