	this.trailTime     = 0;                                // so missile trails can handle dropped initial packets
	// int  dustTrailTime;
	this.miscTime      = 0;

	this.snapshotTime  = 0;                                // last time this entity was found in a snapshot

	this.pe            = new PlayerEntity();
