/**
 * RegisterClients
 */
function RegisterClients(progress) {
	var total = 0;
	for (var i = 0; i < MAX_CLIENTS; i++) {
		var cs = Configstring('player:' + i);
		if (cs) {
			total++;
		}
	}

	var loaded = 0;
	var clientLoaded = function () {
		progress(++loaded / total);
	};

	for (var i = 0; i < MAX_CLIENTS; i++) {
		var cs = Configstring('player:' + i);
		if (!cs) {
			continue;
		}

		NewClientInfo(i, clientLoaded);
	}
}

