	position.array[position.index++] = origin[1] - left[1] - up[1];
	position.array[position.index++] = origin[2] - left[2] - up[2];

	position.array[position.index++] = origin[0] + left[0] - up[0];
	position.array[position.index++] = origin[1] + left[1] - up[1];
	position.array[position.index++] = origin[2] + left[2] - up[2];

