	color.array[color.index++] = modulate[0];
	color.array[color.index++] = modulate[1];
	color.array[color.index++] = modulate[2];
	color.array[color.index++] = modulate[3];

	color.array[color.index++] = modulate[0];
	color.array[color.index++] = modulate[1];
	color.array[color.index++] = modulate[2];
	color.array[color.index++] = modulate[3];

