/**
 * GetDefaultTexture
 */
function GetDefaultTexture() {
	var data = new Uint8Array(64*64*4);

	var offset = 0;
	for (var i = 0; i < 64; i++) {
		for (var j = 0; j < 64; j++) {
			if ((i%16 < 8 && j%16 < 8) || (i%16 >= 8 && j%16 >= 8)) {
				data[offset++] = 127;
				data[offset++] = 168;
				data[offset++] = 255;
				data[offset++] = 255;
			} else {
				data[offset++] = 255;
				data[offset++] = 255;
				data[offset++] = 255;
				data[offset++] = 255;
			}
		}
	}

	var texture = new Texture();
	texture.texnum = BuildTexture(data, 64, 64, false);

	return texture;
}

