	WriteDeltaKey(msg, 0/*key*/, from.rightmove, to.rightmove, 8);
	WriteDeltaKey(msg, 0/*key*/, from.upmove, to.upmove, 8);
	WriteDeltaKey(msg, 0/*key*/, from.buttons, to.buttons, 16);
	WriteDeltaKey(msg, 0/*key*/, from.weapon, to.weapon, 8);
