/**
 * Plane
 */

var PLANE_X           = 0;
var PLANE_Y           = 1;
var PLANE_Z           = 2;
var PLANE_NON_AXIAL   = 3;

var SIDE_FRONT        = 1;
var SIDE_BACK         = 2;
var SIDE_ON           = 3;
var PLANE_TRI_EPSILON = 0.1;

var Plane = function () {
	this.normal   = vec3.create();
	this.dist     = 0;
	this.type     = 0;
	this.signbits = 0;
};

Plane.prototype.clone = function (to) {
	if (typeof(to) === 'undefined') {
		to = new Plane();
	}

	vec3.set(this.normal, to.normal);
	to.dist = this.dist;
	to.type = this.type;
	to.signbits = this.signbits;

	return to;
};

