	if (!self.wait) {
		self.wait = 1;
	}
	self.wait *= 1000;

	// First position
	vec3.set(self.s.origin, self.pos1);

	// Calculate second position.
	sv.SetBrushModel(self, self.model);

	var lip = SpawnFloat('lip', 4);

