	refent.shaderTexCoord[0] = this.shaderTexCoord[0];
	refent.shaderTexCoord[1] = this.shaderTexCoord[1];
	refent.shaderTime = this.shaderTime;
	refent.radius = this.radius;
	refent.rotation = this.rotation;
