		index.data[index.index++] = oindex.data[indexOffset + i + 1];
		index.data[index.index++] = oindex.data[indexOffset + i + 1];
		index.data[index.index++] = oindex.data[indexOffset + i + 2];
		index.data[index.index++] = oindex.data[indexOffset + i + 2];
