		origin[1] = xyz.data[idx * 3 + 1];
		origin[2] = xyz.data[idx * 3 + 2];
		norm[0] = normal.data[idx * 3 + 0];
		norm[1] = normal.data[idx * 3 + 1];
		norm[2] = normal.data[idx * 3 + 2];

