	out.pos[2] = 0.5 * (a.pos[2] + b.pos[2]);

	out.lmCoord[0] = 0.5 * (a.lmCoord[0] + b.lmCoord[0]);
	out.lmCoord[1] = 0.5 * (a.lmCoord[1] + b.lmCoord[1]);

	out.texCoord[0] = 0.5 * (a.texCoord[0] + b.texCoord[0]);
	out.texCoord[1] = 0.5 * (a.texCoord[1] + b.texCoord[1]);

